import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'UTS Mobile',
      debugShowCheckedModeBanner: false,
      home: ShoesPage(),
    );
  }
}

class ShoesPage extends StatelessWidget {
  // Data dummy untuk 5 sepatu
  final List<Map<String, String>> shoes = [
    {
      'image': 'data:image/jpeg;base64,',
      'name': 'Nike SB',
      'price': 'Rp 1.200.000'
    },
    {
      'image': 'data:image/jpeg;base64,',
      'name': 'Adidas Ultraboost',
      'price': 'Rp 1.800.000'
    },
    {
      'image': 'data:image/jpeg;base64,',
      'name': 'Puma Runner',
      'price': 'Rp 950.000'
    },
    {
      'image': 'data:image/jpeg;base64,',
      'name': 'Reebok Flex',
      'price': 'Rp 1.100.000'
    },
    {
      'image': 'data:image/jpeg;base64,',
      'name': 'New Balance 574',
      'price': 'Rp 1.300.000'
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[100],
      appBar: AppBar(
        title: Text('Shoes'),
        backgroundColor: Colors.deepPurple,
        actions: [
          Padding(
            padding: const EdgeInsets.only(right: 16),
            child: CircleAvatar(
              backgroundImage: NetworkImage(
                'https://i.pravatar.cc/150?img=3', // Gambar profil contoh
              ),
            ),
          )
        ],
      ),
      body: Padding(
        padding: const EdgeInsets.all(16),
        child: ListView.builder(
          itemCount: shoes.length,
          itemBuilder: (context, index) {
            final shoe = shoes[index];
            return Card(
              elevation: 4,
              margin: EdgeInsets.symmetric(vertical: 10),
              child: ListTile(
                leading: Image.network(shoe['image']!, width: 60, height: 60, fit: BoxFit.contain),
                title: Text(shoe['name']!, style: TextStyle(fontWeight: FontWeight.bold)),
                subtitle: Text(shoe['price']!, style: TextStyle(color: Colors.grey[700])),
              ),
            );
          },
        ),
      ),
    );
  }
}
